insert into Item (nomeItem, descricao, categoria, custo, moeda, peso)
	values
	('Armadura Leve Acolchoada', 'A armadura acolchoada consiste em camadas de panos acolchoados e batidos.', 'armaduraEscudo', 5, 'po', 4.0),
	('Armadura Leve Couro', 'O peitoral e as ombreiras da armadura de couro são feitos de couro que foi endurecido após ser fervido em óleo. O resto da armadura é feita de materiais mais macios e mais flexíveis', 'armaduraEscudo', 10, 'po', 5.0),
	('Armadura Leve Couro Batido', 'Feita de couro resistente, mas flexível, a armadura de couro batido é reforçada com rebites ou cravos', 'armaduraEscudo', 45, 'po', 6.5),
	('Armadura Média Gibão de Peles', 'Um gibão de peles é um armadura bruta consistindo de peles grossas. É comumente usada por tribos bárbaras, humanoides malignos e outros povos que não têm acesso às ferramentas e materiais necessários para criar uma armadura melhor', 'armaduraEscudo', 10, 'po', 6),
	('Armadura Média Camisão de Malha', 'Feito de anéis de metal intercalados, um camisão de cota de malha é usado entre as camadas de roupa. Essa armadura oferece proteção modesta para a parte superior do corpo de quem a usa e permite que o som dos anéis de metal, friccionados uns contra os outros, sejam amortecidos pelas camadas exteriores.', 'armaduraEscudo', 30, 'po', 10),
	('Armadura Média Brunea', 'Essa armadura consiste em um casaco e calças (e talvez uma saia separada) de couro coberto com peças sobrepostas de metal, assim como as escamas de peixe. O conjunto inclui manoplas', 'armaduraEscudo', 50, 'po', 22.5),
	('Armadura Média Peitoral', 'A armadura peitoral é constituída por um peitoral de metal usado com couro flexível em seu interior. Embora ele deixe as pernas e braços do usuário relativamente desprotegidos, essa armadura fornece boa proteção para seus órgãos vitais, deixando quem a usa relativamente sem restrições.', 'armaduraEscudo', 400, 'po', 10),
	('Armadura Média Meia-Armadura', 'Essa armadura é composta de placas de metal moldadas que cobrem a maior parte do corpo. Ela não inclui proteção para as pernas além de caneleiras fixadas com tiras de couro.', 'armaduraEscudo', 750, 'po', 20),
	('Armadura Pesada Cota de anéis', 'Esta armadura é feita de couro com pesados anéis presos a ela. Os anéis ajudam a reforçar a armadura contra golpes de espadas e machados. A cota de anéis é inferior à cota de malha e geralmente é vestida apenas por aqueles que não podem pagar por uma armadura melhor.', 'armaduraEscudo', 30, 'po', 20),
	('Armadura Pesada Cota de malha', 'Feita de anéis de metal entrelaçados, a cota de malha inclui uma camada de tecido acolchoado usada por baixo da malha de metal para evitar atrito e amortecer o impacto dos golpes. O conjunto inclui manoplas.', 'armaduraEscudo', 75, 'po', 27.5),
	('Armadura Pesada Cota de talas', 'Essa armadura é feita de tiras verticais de metal, rebitadas a um suporte de couro, usadas sobre um preenchimento de pano. Cotas de malha flexíveis protegem as articulações.', 'armaduraEscudo', 200, 'po', 30),
	('Armadura Pesada Placas', 'A armadura de placas consiste em placas de metal moldadas para cobrir todo o corpo. Inclui luvas, botas de couro pesadas, um capacete com viseira e espessas camadas de enchimento por baixo da armadura. Fivelas e tiras de couro distribuem o peso ao longo do corpo.', 'armaduraEscudo', 1500, 'po', 32.5),
	('Escudo', 'Um escudo é feito de madeira ou metal e é usado com uma mão. Empunhar um escudo aumenta sua Classe de Armadura em 2. Você só pode se beneficiar de um escudo por vez.', 'armaduraEscudo', 10, 'po', 3),
